            cs.set = new ButtonSet(`green`, `grey`, `fa-search`, `fa-times`, `Search`, `Cancel`, startCsSearch.bind(null, cs), cancelCsSearch.bind(null, cs));
            cs.popup.description.appendChild(cs.set.set);
